<template>
  <div class="bottomMenu">
    <ul>
      <li @click="switchTitleList">
        <i class="icon icon-menu"></i>
        <span>目录</span>
      </li>
      <li @click="switchFonts">
        <i class="icon icon-ft"></i>
        <span>字体</span>
      </li>
      <li @click="switchNights">
        <i class="icon" :class="night ? 'icon-day' : 'icon-night'"></i>
        <span>{{ night ? "日间" : "夜间" }}</span>
      </li>
    </ul>
  </div>
</template>

<script>
import { mapState } from "vuex";
export default {
  name: "bottomMenu",
  computed: {
    ...mapState({
      night: (state) => state.book.night,
    }),
  },
  methods: {
    //显示/隐藏目录
    switchTitleList() {
      this.$emit("switchList");
    },
    //显示/隐藏字体设置
    switchFonts() {
      this.$store.dispatch("font");
    },
    //日夜模式
    switchNights() {
      this.$store.dispatch("nightState");
    },
  },
};
</script>

